import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router, NavigationEnd, Event } from '@angular/router';
import { Subscription } from 'rxjs';
import { filter } from 'rxjs/operators';

interface Offer {
  id: string;
  title: string;
  description: string;
  image: string;
  buttonText: string;
  category: string;
}

interface QuickAction {
  id: string;
  icon: string;
  title: string;
  subtitle: string;
  route?: string;
}

@Component({
  selector: 'app-citi-home',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './citi-home.component.html',
  styleUrls: ['./citi-home.component.scss']
})
export class CitiHomeComponent implements OnInit, OnDestroy {
  activeTab = 'All';
  tabs = ['All', 'Offers', 'Wealth Insights'];
  showHeader = true;
  private routerSubscription: Subscription = new Subscription();

  constructor(private router: Router) {}

  ngOnInit() {
    // Immediately clear any header hiding states when entering citi-home
    localStorage.removeItem('academyMode');
    localStorage.removeItem('hideHeader');
    
    // Check header visibility on component initialization
    this.checkHeaderVisibility();
    
    // Subscribe to router events to handle navigation changes
    this.routerSubscription = this.router.events
      .pipe(filter((event): event is NavigationEnd => event instanceof NavigationEnd))
      .subscribe((event) => {
        // Small delay to ensure route is fully loaded
        setTimeout(() => {
          this.checkHeaderVisibility();
        }, 50);
      });
  }

  ngOnDestroy() {
    // Clean up subscriptions to prevent memory leaks
    if (this.routerSubscription) {
      this.routerSubscription.unsubscribe();
    }
  }

  offers: Offer[] = [
    {
      id: '1',
      title: 'Citi Gourmet Pleasures',
      description: 'Enjoy up to 50% off dining deals with Citi Cards. T&Cs apply.',
      image: 'gourmet-pleasures.jpg',
      buttonText: 'Learn More',
      category: 'Offers'
    },
    {
      id: '2',
      title: 'Citi Mastercard Million Dollar Giveaway',
      description: 'Win big with our exclusive giveaway program.',
      image: 'mastercard-giveaway.jpg',
      buttonText: 'Find out more',
      category: 'Offers'
    },
    {
      id: '3',
      title: 'Building a diversified portfolio',
      description: 'Learn the fundamentals of portfolio diversification.',
      image: 'portfolio-building.jpg',
      buttonText: 'Read more',
      category: 'Wealth Insights'
    },
    {
      id: '4',
      title: 'Be an informed investor',
      description: 'Stay updated with market trends and investment strategies.',
      image: 'informed-investor.jpg',
      buttonText: 'Learn more',
      category: 'Wealth Insights'
    },
    {
      id: '5',
      title: 'Exploring loan options',
      description: 'There may be situations where taking on a loan could help.',
      image: 'loan-options.jpg',
      buttonText: 'Read more',
      category: 'Wealth Insights'
    },
    {
      id: '6',
      title: 'Citi Private Pass',
      description: 'Exclusive travel experiences and premium airport lounge access worldwide.',
      image: 'travel-private-pass.jpg',
      buttonText: 'Explore',
      category: 'Offers'
    },
    {
      id: '7',
      title: 'Wealth Management',
      description: 'Personalized investment strategies and financial planning for your future.',
      image: 'wealth-management.jpg',
      buttonText: 'Get Started',
      category: 'Wealth Insights'
    }
  ];

  quickActions: QuickAction[] = [
    {
      id: '1',
      icon: 'lock',
      title: 'Authorise',
      subtitle: 'Transaction',
      route: '/authorise'
    },
    {
      id: '2',
      icon: 'money-transfer',
      title: 'PayNow',
      subtitle: '',
      route: '/paynow'
    },
    {
      id: '3',
      icon: 'qr-code',
      title: 'Scan QR',
      subtitle: '',
      route: '/scan-qr'
    },
    {
      id: '4',
      icon: 'menu',
      title: 'More',
      subtitle: '',
      route: '/more'
    }
  ];

  

  setActiveTab(tab: string) {
    this.activeTab = tab;
  }

  getFilteredOffers() {
    if (this.activeTab === 'All') {
      return this.offers;
    }
    return this.offers.filter(offer => offer.category === this.activeTab);
  }

  onOfferClick(offer: Offer) {
    // Handle offer click - could navigate to offer details
    console.log('Offer clicked:', offer);
  }

  onQuickActionClick(action: QuickAction) {
    if (action.route) {
      this.router.navigate([action.route]);
    }
  }

  onLoginClick() {
    // Navigate to dashboard
    this.router.navigate(['/dashboard']);
  }

  onLogoClick() {
    this.router.navigate(['/dashboard']);
  }

  private checkHeaderVisibility() {
    const currentUrl = this.router.url;
    console.log('Current URL:', currentUrl); // Debug log
    
    // Always clear academy mode when arriving at citi-home
    // This ensures clean state regardless of where we came from
    if (currentUrl === '/citi-home' || currentUrl === '/' || currentUrl.includes('citi-home')) {
      localStorage.removeItem('academyMode');
      localStorage.removeItem('hideHeader');
      // Force clear any header hiding flags
      setTimeout(() => {
        localStorage.removeItem('academyMode');
        localStorage.removeItem('hideHeader');
      }, 100);
      this.showHeader = true;
      console.log('Cleared all header hiding flags, showing header'); // Debug log
      return;
    }
    
    // Define routes where header should be hidden
    const hideHeaderRoutes = ['/academy', '/chatbot', '/onboarding'];
    
    // Check if current route should hide header
    const shouldHideBasedOnRoute = hideHeaderRoutes.some(route => currentUrl.includes(route));
    
    // Check for academy mode from localStorage
    const isAcademyMode = localStorage.getItem('academyMode') === 'true';
    console.log('Academy mode:', isAcademyMode); // Debug log
    
    if (shouldHideBasedOnRoute || isAcademyMode) {
      this.showHeader = false;
      console.log('Hiding header'); // Debug log
    } else {
      this.showHeader = true;
      console.log('Showing header'); // Debug log
    }
  }

  toggleHeader() {
    this.showHeader = !this.showHeader;
  }
}
