import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

@Component({
  selector: 'app-chatbot',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './chatbot.component.html',
  styleUrls: ['./chatbot.component.scss']
})
export class ChatbotComponent {
  messages: ChatMessage[] = [];
  currentMessage = '';

  constructor(private router: Router) {
    this.messages.push({
      id: '1',
      type: 'assistant',
      content: 'Good day! I am your investment banking advisor. How may I assist you today?',
      timestamp: new Date()
    });
  }

  sendMessage() {
    if (!this.currentMessage.trim()) return;
    this.currentMessage = '';
  }

  formatTime(timestamp: Date): string {
    return timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  }

  goBack() {
    this.router.navigate(['/discover']);
  }
}
<div class="chatbot-container">
  <div class="chat-header">
    <button class="back-btn" (click)="goBack()">← Back</button>
    <h3>Investment Advisor</h3>
  </div>
  <div class="chat-messages">
    <div *ngFor="let message of messages" class="message" [ngClass]="message.type">
      <div class="message-content">
        <div class="message-text">{{message.content}}</div>
        <div class="message-time">{{formatTime(message.timestamp)}}</div>
      </div>
    </div>
  </div>
  <div class="chat-input">
    <div class="input-container">
      <input 
        type="text" 
        [(ngModel)]="currentMessage" 
        (keyup.enter)="sendMessage()" 
        placeholder="Ask about investments, portfolio advice, or market insights..." 
        class="message-input"
      />
      <button 
        (click)="sendMessage()" 
        class="send-btn" 
        [disabled]="!currentMessage.trim()"
      >
        Send
      </button>
    </div>
  </div>
</div>
