import { Component, ViewChild, ElementRef, AfterViewChecked } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

interface QuestionSet {
  title: string;
  questions: string[];
  context: string;
}

@Component({
  selector: 'app-chatbot',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './chatbot.component.html',
  styleUrls: ['./chatbot.component.scss']
})
export class ChatbotComponent implements AfterViewChecked {
  @ViewChild('chatMessages') private chatMessages!: ElementRef;
  @ViewChild('messageInput') private messageInput!: ElementRef;
  
  messages: ChatMessage[] = [];
  currentMessage = '';
  isTyping = false;
  isRefreshing = false;
  currentQuestionSetIndex = 0;
  private shouldScrollToBottom = false;

  questionSets: QuestionSet[] = [
    {
      title: "Getting Started",
      context: "beginner",
      questions: [
        "How to start investing with $1000?",
        "What's the difference between stocks and bonds?",
        "Explain compound interest simply",
        "Best investment apps for beginners",
        "How much should I save before investing?",
        "What is diversification?"
      ]
    },
    {
      title: "Portfolio Management",
      context: "portfolio",
      questions: [
        "Help me rebalance my portfolio",
        "Best asset allocation for my age",
        "How to diversify across sectors?",
        "Should I invest in international markets?",
        "When to rebalance my investments?",
        "Tax-efficient portfolio strategies"
      ]
    },
    {
      title: "Market Analysis",
      context: "market",
      questions: [
        "What's the current market outlook?",
        "Which sectors are performing well?",
        "How do interest rates affect stocks?",
        "Is now a good time to invest?",
        "Market volatility strategies",
        "Economic indicators to watch"
      ]
    },
    {
      title: "Advanced Strategies",
      context: "advanced",
      questions: [
        "Options trading basics",
        "Value vs growth investing",
        "Dollar-cost averaging strategy",
        "Real estate investment trusts (REITs)",
        "ESG investing opportunities",
        "Cryptocurrency portfolio allocation"
      ]
    },
    {
      title: "Risk Management",
      context: "risk",
      questions: [
        "How to protect my portfolio in a recession?",
        "Stop-loss order strategies",
        "Emergency fund vs investments",
        "Insurance needs assessment",
        "Hedging strategies for beginners",
        "Risk tolerance evaluation"
      ]
    },
    {
      title: "Retirement Planning",
      context: "retirement",
      questions: [
        "401(k) vs Roth IRA comparison",
        "How much do I need for retirement?",
        "Social Security optimization",
        "Healthcare costs in retirement",
        "Retirement withdrawal strategies",
        "Catch-up contributions explained"
      ]
    }
  ];

  suggestions = [
    "What's the current market outlook?",
    "Help me diversify my portfolio",
    "Explain cryptocurrency investing",
    "Best long-term investment strategies",
    "How to start investing with $1000?",
    "Risk management techniques"
  ];

  get currentQuestionSet(): QuestionSet {
    return this.questionSets[this.currentQuestionSetIndex];
  }

  constructor(private router: Router) {
    // Start with empty messages to show welcome screen
  }

  selectSuggestion(suggestion: string) {
    this.currentMessage = suggestion;
    this.sendMessage();
  }

  refreshQuestions() {
    if (this.isRefreshing) return;
    
    this.isRefreshing = true;
    
    // Cycle to next question set
    setTimeout(() => {
      this.currentQuestionSetIndex = (this.currentQuestionSetIndex + 1) % this.questionSets.length;
      this.isRefreshing = false;
    }, 600); // Animation duration
  }

  sendMessage() {
    if (!this.currentMessage.trim() || this.isTyping) return;
    
    // Add user message
    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      type: 'user',
      content: this.currentMessage,
      timestamp: new Date()
    };
    this.messages.push(userMessage);
    this.shouldScrollToBottom = true;
    
    // Store current message for response generation
    const userInput = this.currentMessage;
    this.currentMessage = '';
    this.resizeTextarea();
    
    // Show typing indicator
    this.isTyping = true;
    this.shouldScrollToBottom = true;
    
    // Generate AI response after a realistic delay
    setTimeout(() => {
      const response = this.generateInvestmentResponse(userInput);
      const aiMessage: ChatMessage = {
        id: (Date.now() + 1).toString(),
        type: 'assistant',
        content: response,
        timestamp: new Date()
      };
      this.messages.push(aiMessage);
      this.isTyping = false;
      this.shouldScrollToBottom = true;
    }, 1500 + Math.random() * 1000); // 1.5-2.5 seconds for realistic feel
  }

  onKeyDown(event: KeyboardEvent) {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      this.sendMessage();
    }
  }

  onInput(event: any) {
    this.resizeTextarea();
  }

  private resizeTextarea() {
    if (this.messageInput?.nativeElement) {
      const textarea = this.messageInput.nativeElement;
      textarea.style.height = 'auto';
      textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';
    }
  }

  ngAfterViewChecked() {
    if (this.shouldScrollToBottom) {
      this.scrollToBottom();
      this.shouldScrollToBottom = false;
    }
  }

  private scrollToBottom(): void {
    try {
      if (this.chatMessages?.nativeElement) {
        this.chatMessages.nativeElement.scrollTop = this.chatMessages.nativeElement.scrollHeight;
      }
    } catch(err) {
      console.log('Could not scroll to bottom:', err);
    }
  }

