import { Component, ViewChild, ElementRef, AfterViewChecked } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

interface QuestionSet {
  title: string;
  questions: string[];
  context: string;
}

@Component({
  selector: 'app-chatbot',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './chatbot.component.html',
  styleUrls: ['./chatbot.component.scss']
})
export class ChatbotComponent implements AfterViewChecked {
  @ViewChild('chatMessages') private chatMessages!: ElementRef;
  @ViewChild('messageInput') private messageInput!: ElementRef;
  
  messages: ChatMessage[] = [];
  currentMessage = '';
  isTyping = false;
  isRefreshing = false;
  currentQuestionSetIndex = 0;
  private shouldScrollToBottom = false;

  questionSets: QuestionSet[] = [
    {
      title: "Getting Started",
      context: "beginner",
      questions: [
        "How to start investing with $1000?",
        "What's the difference between stocks and bonds?",
        "Explain compound interest simply",
        "Best investment apps for beginners",
        "How much should I save before investing?",
        "What is diversification?"
      ]
    },
    {
      title: "Portfolio Management",
      context: "portfolio",
      questions: [
        "Help me rebalance my portfolio",
        "Best asset allocation for my age",
        "How to diversify across sectors?",
        "Should I invest in international markets?",
        "When to rebalance my investments?",
