  get currentQuestionSet(): QuestionSet {
    return this.questionSets[this.currentQuestionSetIndex];
  }

  constructor(private router: Router) {
    // Start with empty messages to show welcome screen
  }

  selectSuggestion(suggestion: string) {
    this.currentMessage = suggestion;
    this.sendMessage();
  }

  refreshQuestions() {
    if (this.isRefreshing) return;
    
    this.isRefreshing = true;
    
    // Cycle to next question set
    setTimeout(() => {
      this.currentQuestionSetIndex = (this.currentQuestionSetIndex + 1) % this.questionSets.length;
      this.isRefreshing = false;
    }, 600); // Animation duration
  }

  sendMessage() {
    if (!this.currentMessage.trim() || this.isTyping) return;
    
    // Add user message
    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      type: 'user',
      content: this.currentMessage,
      timestamp: new Date()
    };
    this.messages.push(userMessage);
    this.shouldScrollToBottom = true;
    
    // Store current message for response generation
    const userInput = this.currentMessage;
    this.currentMessage = '';
    this.resizeTextarea();
    
    // Show typing indicator
    this.isTyping = true;
    this.shouldScrollToBottom = true;
    
    // Generate AI response after a realistic delay
    setTimeout(() => {
      const response = this.generateInvestmentResponse(userInput);
      const aiMessage: ChatMessage = {
        id: (Date.now() + 1).toString(),
        type: 'assistant',
        content: response,
        timestamp: new Date()
      };
      this.messages.push(aiMessage);
      this.isTyping = false;
      this.shouldScrollToBottom = true;
    }, 1500 + Math.random() * 1000); // 1.5-2.5 seconds for realistic feel
  }

  onKeyDown(event: KeyboardEvent) {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      this.sendMessage();
    }
  }

  onInput(event: any) {
    this.resizeTextarea();
  }

  private resizeTextarea() {
    if (this.messageInput?.nativeElement) {
      const textarea = this.messageInput.nativeElement;
      textarea.style.height = 'auto';
      textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';
    }
  }

  ngAfterViewChecked() {
    if (this.shouldScrollToBottom) {
      this.scrollToBottom();
      this.shouldScrollToBottom = false;
    }
  }

  private scrollToBottom(): void {
    try {
      if (this.chatMessages?.nativeElement) {
        this.chatMessages.nativeElement.scrollTop = this.chatMessages.nativeElement.scrollHeight;
      }
    } catch(err) {
      console.log('Could not scroll to bottom:', err);
    }
  }

