import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';
import { NavigationService } from '../../services/navigation.service';

interface SignupStep {
  id: number;
  title: string;
  description: string;
  icon: string;
  status: 'pending' | 'current' | 'completed';
  details: string[];
}

@Component({
  selector: 'app-signup',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent implements OnInit {
  currentStep = 1;
  
  steps: SignupStep[] = [
    {
      id: 1,
      title: 'Choose Account Type',
      description: 'Select the account that best fits your banking needs',
      icon: '🏦',
      status: 'current',
      details: [
        'Savings Account - Earn interest on your deposits',
        'Current Account - For everyday banking transactions',
        'Premium Account - Enhanced benefits and services',
        'Investment Account - Grow your wealth with our investment products'
      ]
    },
    {
      id: 2,
      title: 'Prepare Required Documents',
      description: 'Gather all necessary documents for your application',
      icon: '📄',
      status: 'pending',
      details: [
        'Valid NRIC or Passport',
        'Proof of Income (Latest 3 months payslips)',
        'Proof of Address (Utility bills or bank statements)',
        'Employment Letter or Business Registration'
      ]
    },
    {
      id: 3,
      title: 'Submit Your Application',
      description: 'Complete and submit your account opening application',
      icon: '📝',
      status: 'pending',
      details: [
        'Fill in personal and contact information',
        'Upload required documents',
        'Set up your initial deposit amount',
        'Review and submit your application'
      ]
    },
    {
      id: 4,
      title: 'Complete Verification Process',
      description: 'Verify your identity and complete KYC requirements',
      icon: '✅',
      status: 'pending',
      details: [
        'Identity verification via video call or branch visit',
        'Document verification by our team',
        'Background checks and credit assessment',
        'Receive verification confirmation'
      ]
    },
    {
      id: 5,
      title: 'Activate Your Account',
      description: 'Activate your account and start banking with Citi',
      icon: '🎉',
      status: 'pending',
      details: [
        'Receive your debit card and welcome kit',
        'Set up online and mobile banking',
        'Make your first deposit',
        'Start enjoying Citi banking services'
      ]
    }
  ];

  constructor(private router: Router, private navigationService: NavigationService) {}

  ngOnInit() {
    // Set page name for header
    this.navigationService.setCurrentPage('Sign Up');
    
    // Update steps status based on current step
    this.updateStepsStatus();
  }

  updateStepsStatus() {
    this.steps.forEach(step => {
      if (step.id < this.currentStep) {
        step.status = 'completed';
      } else if (step.id === this.currentStep) {
        step.status = 'current';
      } else {
        step.status = 'pending';
      }
    });
  }

  goToStep(stepId: number) {
    if (stepId <= this.currentStep + 1) {
      this.currentStep = stepId;
      this.updateStepsStatus();
    }
  }

  nextStep() {
    if (this.currentStep < this.steps.length) {
      this.currentStep++;
      this.updateStepsStatus();
    }
  }

  previousStep() {
    if (this.currentStep > 1) {
      this.currentStep--;
      this.updateStepsStatus();
    }
  }

  startApplication() {
    // In a real app, this would navigate to the actual application form
    alert('Application process will start. This would typically navigate to a detailed application form.');
  }

  goBack() {
    this.router.navigate(['/citi-home']).then(() => {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    });
  }

  getCurrentStep(): SignupStep {
    return this.steps.find(step => step.id === this.currentStep) || this.steps[0];
  }
}
