import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';

interface AccountStat {
  label: string;
  value: string;
  icon: string;
  type: 'miles' | 'points' | 'cashback' | 'smr';
  accountNumber: string;
}

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent {
  userName = 'Vivek Kamaraj';
  
  accountStats: AccountStat[] = [
    { 
      label: 'Miles', 
      value: '62,400', 
      icon: 'airplane', 
      type: 'miles',
      accountNumber: '••••7855'
    },
    { 
      label: 'Points', 
      value: '143,823', 
      icon: 'star', 
      type: 'points',
      accountNumber: '••••3707'
    },
    { 
      label: 'Cash Back', 
      value: '0.75', 
      icon: 'cashback', 
      type: 'cashback',
      accountNumber: '••••2879'
    },
    { 
      label: 'Citi SMR', 
      value: '', 
      icon: 'smr', 
      type: 'smr',
      accountNumber: ''
    }
  ];

  wanderPlusOffer = {
    title: 'Complimentary AIA WanderPlus Cover',
    description: 'Stay protected with free travel and personal accident coverage. T&Cs apply.'
  };

  savingsSection = {
    title: 'MORE SAVINGS AND CONVENIENCE',
    subtitle: 'Zero foreign exchange fees with Citibank Global Wallet',
    buttonText: 'Try now!'
  };

  totalAssets = {
    amount: 'SGD 919.48'
  };

  localDeposits = {
    amount: 'SGD 919.48'
  };

  foreignDeposits = {
    amount: 'SGD 0.00'
  };

  creditCards = {
    amount: 'SGD 319.01',
    label: 'REPLACEMENT CARD'
  };

  replacementCard = {
    title: 'Activate your replacement Citi Cash Back®',
    cardNumber: '••••2879'
  };

  bottomNavItems = [
    { label: 'Wealth', icon: 'chart', route: '/wealth' },
    { label: 'Payments', icon: 'transfer', route: '/payments' },
    { label: 'For You', icon: 'user', route: '/academy' }
  ];

  constructor(private router: Router) {}

  onWanderPlusClick() {
    console.log('WanderPlus offer clicked');
  }

  onTryNowClick() {
    console.log('Try now clicked');
  }

  onWealthAnalysisClick() {
    console.log('Wealth Analysis clicked');
  }

  onEStatementsClick() {
    console.log('E-Statements clicked');
  }

  onTotalAssetsClick() {
    console.log('Total Assets clicked');
  }

  onLocalDepositsClick() {
    console.log('Local Currency Deposits clicked');
  }

  onForeignDepositsClick() {
    console.log('Foreign Currency Deposits clicked');
  }

  onCreditCardsClick() {
    console.log('Credit Cards clicked');
  }

  onActivateCardClick() {
    console.log('Activate replacement card clicked');
  }

  onBottomNavClick(item: any) {
    if (item.route) {
      this.router.navigate([item.route]);
    }
  }

  goToAcademy() {
    this.router.navigate(['/academy']);
  }
}
