import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';

interface AccountStat {
  label: string;
  value: string;
  icon: string;
  type: 'miles' | 'points' | 'cashback' | 'smr';
}

interface LoanInfo {
  title: string;
  amount: string;
}

interface InsuranceInfo {
  title: string;
  description: string;
}

interface PromoOffer {
  title: string;
  description: string;
  icon: string;
}

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent {
  userName = 'Vivek Kamaraj';
  
  accountStats: AccountStat[] = [
    { label: 'Miles ••••7855', value: '62,400', icon: 'airplane', type: 'miles' },
    { label: 'Points ••••3707', value: '143,823', icon: 'star', type: 'points' },
    { label: 'Cash Back ••••2879', value: '0.75', icon: 'cashback', type: 'cashback' },
    { label: 'Citi SMR', value: '', icon: 'smr', type: 'smr' }
  ];

  cashBackActivation = {
    title: 'Activate your replacement Citi Cash Back®',
    cardNumber: '2879'
  };

  loanInfo: LoanInfo = {
    title: 'Loans',
    amount: 'SGD 0.00'
  };

  insuranceInfo: InsuranceInfo = {
    title: 'Insurance',
    description: 'Start protecting yourself and your loved ones'
  };

  relationshipGrowth = {
    title: 'Grow your Relationship'
  };

  wanderPlusOffer: PromoOffer = {
    title: 'Complimentary AIA WanderPlus Cover',
    description: 'Stay protected with free travel and personal accident coverage. T&Cs apply.',
    icon: 'shield'
  };

  quickCashOffer = {
    title: 'Citi Quick Cash:',
    description: 'Enjoy a pre-approved loan of up to SGD 102,505.00 from your Ready Credit available limit.',
    cta: 'Get it now'
  };

  disclaimerText = 'The Account(s) summary balance will be shown in the Singapore dollar equivalent. The exchange rates used for calculating the Singapore dollar equivalent are indicative only and may differ from the exchange rate applied on actual transactions.';

  bottomNavItems = [
    { label: 'Wealth', icon: 'chart', route: '/wealth' },
    { label: 'Payments', icon: 'transfer', route: '/payments' },
    { label: 'For You', icon: 'user', route: '/for-you' }
  ];

  constructor(private router: Router) {}

  onActivateCard() {
    // Handle card activation
    console.log('Activate card clicked');
  }

  onLoanExpand() {
    // Handle loan section expand
    console.log('Loan section expanded');
  }

  onInsuranceExpand() {
    // Handle insurance section expand
    console.log('Insurance section expanded');
  }

  onGrowRelationship() {
    // Handle grow relationship click
    console.log('Grow relationship clicked');
  }

  onWanderPlusClick() {
    // Handle WanderPlus offer click
    console.log('WanderPlus offer clicked');
  }

  onQuickCashClick() {
    // Handle Quick Cash click
    console.log('Quick Cash clicked');
  }

  onBottomNavClick(item: any) {
    if (item.route) {
      this.router.navigate([item.route]);
    }
  }

  goToAcademy() {
    this.router.navigate(['/academy']);
  }
}
