    // Handle navigation to different sections
    switch(section) {
      case 'net-worth':
        console.log('Navigating to Net Worth assessment');
        break;
      case 'risk-profile':
        console.log('Navigating to Risk Profile Questionnaire');
        break;
      case 'knowledge-assessment':
