import { Component } from "@angular/core";
import { CommonModule } from "@angular/common";
import { Router } from "@angular/router";

interface ConstituentStock {
  symbol: string;
  lastPrice: number;
  changePercent: number;
  isPositive: boolean;
}

@Component({
  selector: "app-stock-detail",
  standalone: true,
  imports: [CommonModule],
  templateUrl: "./stock-detail.component.html",
  styleUrls: ["./stock-detail.component.scss"],
})
export class StockDetailComponent {
  stockInfo = {
    symbol: "BK4523",
    name: "Indian Concept",
    market: "US",
    price: 1728.54,
    change: 13.44,
    changePercent: 0.78,
    isPositive: true,
    status: "Not Yet Opened",
    openTime: "10/21 16:00:00 EDT",
    high: 1729.38,
    low: 1709.55,
    open: 1715.32,
    prevClose: 1715.10,
    volume: "46.31M",
    turnoverRate: 0.28,
    peRatio: 20.39,
    pbRatio: 3.21,
    marketCap: "440.2B",
    amplitude: 1.16,
    floatCap: "402.9B",
    gainers: 5,
    unchanged: 0,
    losers: 11
  };

  selectedTimeframe = "D";
  timeframes = ["1D", "5D", "D", "W", "M", "Q", "Y"];

  constituentStocks: ConstituentStock[] = [
    { symbol: "RELIANCE.NS", lastPrice: 2456.80, changePercent: 2.34, isPositive: true },
    { symbol: "TCS.NS", lastPrice: 3245.60, changePercent: 1.87, isPositive: true },
    { symbol: "HDFCBANK.NS", lastPrice: 1678.90, changePercent: -0.45, isPositive: false },
    { symbol: "INFY.NS", lastPrice: 1534.25, changePercent: 1.23, isPositive: true },
    { symbol: "HINDUNILVR.NS", lastPrice: 2387.45, changePercent: 0.89, isPositive: true },
  ];

  constructor(private router: Router) {}

  goBack() {
    this.router.navigate(["/discover"]);
  }

  selectTimeframe(timeframe: string) {
    this.selectedTimeframe = timeframe;
  }

  openAIChat() {
    this.router.navigate(["/chatbot"]);
  }
}
