import { Component, OnInit, OnDestroy } from "@angular/core";
import { CommonModule } from "@angular/common";
import { Router, NavigationEnd } from "@angular/router";
import { filter } from "rxjs/operators";

@Component({
  selector: "app-header",
  standalone: true,
  imports: [CommonModule],
  templateUrl: "./header.html",
  styleUrls: ["./header.scss"],
})
export class HeaderComponent implements OnInit, OnDestroy {
  currentTime = "12:53";
  currentTab = "academy";
  private timeInterval: any;

  constructor(private router: Router) {}

  ngOnInit() {
    this.updateTime();
    this.timeInterval = setInterval(() => this.updateTime(), 60000);
    
    // Set initial tab based on current route
    this.setTabFromRoute();
    
    // Listen to route changes
    this.router.events
      .pipe(filter(event => event instanceof NavigationEnd))
      .subscribe(() => this.setTabFromRoute());
  }

  ngOnDestroy() {
    if (this.timeInterval) {
      clearInterval(this.timeInterval);
    }
  }

  updateTime() {
    const now = new Date();
    this.currentTime = now.toLocaleTimeString("en-US", {
      hour: "2-digit",
      minute: "2-digit",
      hour12: false,
    });
  }

  setActiveTab(tab: string) {
    this.currentTab = tab;
    
    // Navigate to the selected tab
    switch (tab) {
      case 'discover':
        this.router.navigate(['/discover']);
        break;
      case 'academy':
        this.router.navigate(['/academy']);
        break;
      case 'portfolio':
        this.router.navigate(['/portfolio']);
        break;
    }
  }

  private setTabFromRoute() {
    const url = this.router.url;
    if (url.includes('/discover')) {
      this.currentTab = 'discover';
    } else if (url.includes('/academy')) {
      this.currentTab = 'academy';
    } else if (url.includes('/portfolio')) {
      this.currentTab = 'portfolio';
    } else {
      this.currentTab = 'academy'; // default
    }
  }
}
