interface TradingGroup {
  title: string;
  subtitle: string;
  stocks: Stock[];
}

interface DividendItem {
  id: string;
  symbol: string;
  companyName: string;
  market: string;
  dividend: number;
  exDate?: string;
  recordDate?: string;
  color: string;
  letter: string;
