import { Component, ViewChild, ElementRef, AfterViewChecked } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

@Component({
  selector: 'app-chatbot',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './chatbot.component.html',
  styleUrls: ['./chatbot.component.scss']
})
export class ChatbotComponent implements AfterViewChecked {
  @ViewChild('chatMessages') private chatMessages!: ElementRef;
  messages: ChatMessage[] = [];
  currentMessage = '';
  private shouldScrollToBottom = false;

  constructor(private router: Router) {
    this.messages.push({
      id: '1',
      type: 'assistant',
      content: 'Good day! I am your investment banking advisor. How may I assist you today?',
      timestamp: new Date()
    });
  }

  sendMessage() {
    if (!this.currentMessage.trim()) return;
    
    // Add user message
    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      type: 'user',
      content: this.currentMessage,
      timestamp: new Date()
    };
    this.messages.push(userMessage);
    this.shouldScrollToBottom = true;
    
    // Store current message for response generation
    const userInput = this.currentMessage;
    this.currentMessage = '';
    
    // Generate AI response after a short delay
    setTimeout(() => {
      const response = this.generateInvestmentResponse(userInput);
      const aiMessage: ChatMessage = {
        id: (Date.now() + 1).toString(),
        type: 'assistant',
        content: response,
        timestamp: new Date()
      };
      this.messages.push(aiMessage);
      this.shouldScrollToBottom = true;
    }, 1000);
  }

  ngAfterViewChecked() {
    if (this.shouldScrollToBottom) {
      this.scrollToBottom();
      this.shouldScrollToBottom = false;
    }
  }

  private scrollToBottom(): void {
    try {
      this.chatMessages.nativeElement.scrollTop = this.chatMessages.nativeElement.scrollHeight;
    } catch(err) {
      console.log('Could not scroll to bottom:', err);
    }
  }

  private generateInvestmentResponse(userInput: string): string {
    const input = userInput.toLowerCase();
    
    const responses = [
      'Based on current market analysis, I recommend diversifying your portfolio across technology, healthcare, and renewable energy sectors.',
      'Our latest research indicates strong growth potential in emerging markets, particularly in Asia-Pacific regions.',
      'Consider dollar-cost averaging for volatile positions to minimize risk exposure while maintaining upside potential.',
      'The Federal Reserve\'s monetary policy suggests maintaining defensive positions in fixed income securities.',
      'ESG investing continues to show strong performance metrics with lower volatility compared to traditional indices.',
      'Current market volatility presents opportunities for value investing in fundamentally strong companies.',
      'I recommend reviewing your risk tolerance and adjusting your asset allocation accordingly for optimal returns.'
    ];
    
    // Simple keyword-based responses
    if (input.includes('portfolio') || input.includes('invest')) {
      return responses[0];
    } else if (input.includes('market') || input.includes('trend')) {
      return responses[1];
    } else if (input.includes('risk') || input.includes('safe')) {
      return responses[2];
    } else {
      // Return a random professional response
      return responses[Math.floor(Math.random() * responses.length)];
    }
  }

  formatTime(timestamp: Date): string {
    return timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  }

  goBack() {
    this.router.navigate(['/discover']);
  }
}
<div class="ai-assist-container">
  <!-- Header -->
  <div class="ai-header">
    <button class="back-btn" (click)="goBack()">
      <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
        <path d="M19 12H5M12 19L5 12L12 5" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
      </svg>
    </button>
    <div class="header-title">
      <h1>AI Assist</h1>
      <p>Your intelligent financial advisor</p>
    </div>
    <div class="header-actions">
      <button class="action-btn">
        <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
          <circle cx="12" cy="12" r="3" stroke="currentColor" stroke-width="2"/>
          <path d="M12 1V3M12 21V23M4.22 4.22L5.64 5.64M18.36 18.36L19.78 19.78M1 12H3M21 12H23M4.22 19.78L5.64 18.36M18.36 5.64L19.78 4.22" stroke="currentColor" stroke-width="2"/>
        </svg>
      </button>
    </div>
  </div>

  <!-- Welcome Screen (when no messages) -->
  <div class="welcome-screen" *ngIf="messages.length === 0">
    <div class="welcome-content">
      <div class="ai-logo">
        <svg width="48" height="48" viewBox="0 0 24 24" fill="none">
          <path d="M9.663 17H7.637C7.293 17 7.017 16.672 7.017 16.267V13.267C7.017 12.862 7.293 12.533 7.637 12.533H9.663C10.007 12.533 10.283 12.862 10.283 13.267V16.267C10.283 16.672 10.007 17 9.663 17Z" fill="currentColor"/>
          <path d="M16.363 17H14.337C13.993 17 13.717 16.672 13.717 16.267V10.267C13.717 9.862 13.993 9.533 14.337 9.533H16.363C16.707 9.533 16.983 9.862 16.983 10.267V16.267C16.983 16.672 16.707 17 16.363 17Z" fill="currentColor"/>
          <path d="M3.5 2V22H20.5V2H3.5ZM19.5 21H4.5V3H19.5V21Z" fill="currentColor"/>
        </svg>
      </div>
      <h2>How can I help you today?</h2>
      <p>Ask me anything about investments, market trends, or financial planning</p>
      
      <!-- Suggested Questions -->
      <div class="suggested-questions">
        <button class="suggestion-chip" *ngFor="let suggestion of suggestions" (click)="selectSuggestion(suggestion)">
          {{suggestion}}
        </button>
      </div>
    </div>
  </div>

  <!-- Chat Messages -->
  <div class="chat-messages" #chatMessages *ngIf="messages.length > 0">
    <div *ngFor="let message of messages; let i = index" class="message-wrapper">
      <div class="message" [ngClass]="message.type + '-message'">
        <div class="message-avatar" *ngIf="message.type === 'assistant'">
          <div class="avatar ai-avatar">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
              <path d="M9.663 17H7.637C7.293 17 7.017 16.672 7.017 16.267V13.267C7.017 12.862 7.293 12.533 7.637 12.533H9.663C10.007 12.533 10.283 12.862 10.283 13.267V16.267C10.283 16.672 10.007 17 9.663 17Z" fill="currentColor"/>
              <path d="M16.363 17H14.337C13.993 17 13.717 16.672 13.717 16.267V10.267C13.717 9.862 13.993 9.533 14.337 9.533H16.363C16.707 9.533 16.983 9.862 16.983 10.267V16.267C16.983 16.672 16.707 17 16.363 17Z" fill="currentColor"/>
              <path d="M3.5 2V22H20.5V2H3.5ZM19.5 21H4.5V3H19.5V21Z" fill="currentColor"/>
            </svg>
          </div>
        </div>
        
        <div class="message-content">
          <div class="message-text" [innerHTML]="formatMessage(message.content)"></div>
          <div class="message-meta">
            <span class="message-time">{{formatTime(message.timestamp)}}</span>
            <div class="message-actions" *ngIf="message.type === 'assistant'">
              <button class="action-btn copy-btn" (click)="copyMessage(message.content)" title="Copy">
                <svg width="14" height="14" viewBox="0 0 24 24" fill="none">
                  <path d="M16 4H18C19.1046 4 20 4.89543 20 6V18C20 19.1046 19.1046 20 18 20H6C4.89543 20 4 19.1046 4 18V6C4 4.89543 4.89543 4 6 4H8" stroke="currentColor" stroke-width="2"/>
                  <rect x="8" y="2" width="8" height="4" rx="1" stroke="currentColor" stroke-width="2"/>
                </svg>
              </button>
              <button class="action-btn thumbs-btn" title="Good response">
                <svg width="14" height="14" viewBox="0 0 24 24" fill="none">
                  <path d="M7 10V20H21V10H7Z" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                  <path d="M7 10L12 5L13 5C13.5523 5 14 5.44772 14 6V9H20L21 10V19L20 20H8L7 19V10Z" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                </svg>
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
    
    <!-- Typing Indicator -->
    <div class="message typing-message" *ngIf="isTyping">
      <div class="message-avatar">
        <div class="avatar ai-avatar">
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
            <path d="M9.663 17H7.637C7.293 17 7.017 16.672 7.017 16.267V13.267C7.017 12.862 7.293 12.533 7.637 12.533H9.663C10.007 12.533 10.283 12.862 10.283 13.267V16.267C10.283 16.672 10.007 17 9.663 17Z" fill="currentColor"/>
            <path d="M16.363 17H14.337C13.993 17 13.717 16.672 13.717 16.267V10.267C13.717 9.862 13.993 9.533 14.337 9.533H16.363C16.707 9.533 16.983 9.862 16.983 10.267V16.267C16.983 16.672 16.707 17 16.363 17Z" fill="currentColor"/>
            <path d="M3.5 2V22H20.5V2H3.5ZM19.5 21H4.5V3H19.5V21Z" fill="currentColor"/>
          </svg>
        </div>
      </div>
      <div class="message-content">
        <div class="typing-indicator">
          <div class="typing-dots">
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </div>
    </div>
  </div>

  <!-- Input Container -->
  <div class="input-container">
    <div class="input-wrapper">
      <div class="input-field">
        <textarea 
          [(ngModel)]="currentMessage" 
          (keydown)="onKeyDown($event)"
          (input)="onInput($event)"
          placeholder="Ask me anything about finance, investments, or market analysis..."
          class="message-input"
          rows="1"
          #messageInput
        ></textarea>
        <button 
          (click)="sendMessage()" 
          class="send-btn" 
          [disabled]="!currentMessage.trim() || isTyping"
          [class.has-content]="currentMessage.trim()"
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
            <path d="M22 2L11 13M22 2L15 22L11 13M22 2L2 9L11 13" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
          </svg>
        </button>
      </div>
    </div>
  </div>
</div>
