ntent-%COMP%]   .input-field[_ngcontent-%COMP%]   .send-btn[_ngcontent-%COMP%] {\n    width: 36px;\n    height: 36px;\n  }\n}\n.chat-info[_ngcontent-%COMP%] {\n  flex: 1;\n}\n\n.chat-participants[_ngcontent-%COMP%] {\n  display: flex;\n  align-items: center;\n  gap: var(--spacing-sm);\n}\n\n.participant-avatars[_ngcontent-%COMP%] {\n  display: flex;\n  gap: -8px;\n}\n\n.avatar[_ngcontent-%COMP%] {\n  width: 36px;\n  height: 36px;\n  border-radius: 50%;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  font-size: var(--font-size-lg);\n  border: 2px solid var(--background-primary);\n  margin-left: -4px;\n}\n\n.avatar.vivek[_ngcontent-%COMP%] {\n  background: #4CAF50;\n  z-index: 3;\n}\n\n.avatar.manoj[_ngcontent-%COMP%] {\n  background: #2196F3;\n  z-index: 2;\n}\n\n.avatar.ai[_ngcontent-%COMP%] {\n  background: #FFD700;\n  z-index: 1;\n}\n\n.avatar.user[_ngcontent-%COMP%] {\n  background: #4CAF50;\n}\n\n.chat-details[_ngcontent-%COMP%] {\n  flex: 1;\n}\n\n.chat-title[_ngcontent-%COMP%] {\n  font-size: 16px;\n  font-weight: 600;\n  color: #000;\n  margin: 0 0 2px 0;\n}\n\n.chat-date[_ngcontent-%COMP%] {\n  font-size: 12px;\n  color: #8e8e93;\n  margin: 0;\n}\n\n.more-btn[_ngcontent-%COMP%] {\n  background: none;\n  border: none;\n  font-size: 20px;\n  color: #8e8e93;\n  cursor: pointer;\n  padding: 8px;\n}\n\n.chat-messages[_ngcontent-%COMP%] {\n  flex: 1;\n  overflow-y: auto;\n  padding: 16px;\n  display: flex;\n  flex-direction: column;\n  gap: 16px;\n  margin-bottom: 80px;\n}\n\n.message-group[_ngcontent-%COMP%] {\n  display: flex;\n  flex-direction: column;\n}\n\n.message[_ngcontent-%COMP%] {\n  display: flex;\n  gap: 12px;\n  margin-bottom: 12px;\n}\n\n.user-message[_ngcontent-%COMP%] {\n  flex-direction: row-reverse;\n}\n\n.message-avatar[_ngcontent-%COMP%] {\n  flex-shrink: 0;\n}\n\n.user-message[_ngcontent-%COMP%]   .avatar[_ngcontent-%COMP%] {\n  background: #4CAF50;\n}\n\n.ai-message[_ngcontent-%COMP%]   .avatar[_ngcontent-%COMP%] {\n  background: #FFD700;\n}\n\n.message-content[_ngcontent-%COMP%] {\n  max-width: 70%;\n  min-width: 120px;\n}\n\n.user-message[_ngcontent-%COMP%]   .message-content[_ngcontent-%COMP%] {\n  align-items: flex-end;\n}\n\n.message-header[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: space-between;\n  align-items: center;\n  margin-bottom: 4px;\n  gap: 8px;\n}\n\n.user-message[_ngcontent-%COMP%]   .message-header[_ngcontent-%COMP%] {\n  flex-direction: row-reverse;\n}\n\n.sender-name[_ngcontent-%COMP%] {\n  font-size: 12px;\n  font-weight: 600;\n  color: #8e8e93;\n}\n\n.message-time[_ngcontent-%COMP%] {\n  font-size: 11px;\n  color: #c7c7cc;\n}\n\n.message-text[_ngcontent-%COMP%] {\n  background: white;\n  padding: 12px 16px;\n  border-radius: 18px;\n  font-size: 14px;\n  line-height: 1.4;\n  color: #000;\n  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);\n  white-space: pre-line;\n}\n\n.user-message[_ngcontent-%COMP%]   .message-text[_ngcontent-%COMP%] {\n  background: #007AFF;\n  color: white;\n  border-bottom-right-radius: 4px;\n}\n\n.ai-message[_ngcontent-%COMP%]   .message-text[_ngcontent-%COMP%] {\n  background: white;\n  border-bottom-left-radius: 4px;\n}\n\n.typing-indicator[_ngcontent-%COMP%] {\n  background: white;\n  padding: 12px 16px;\n  border-radius: 18px;\n  border-bottom-left-radius: 4px;\n  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);\n}\n\n.typing-dots[_ngcontent-%COMP%] {\n  display: flex;\n  gap: 4px;\n}\n\n.typing-dots[_ngcontent-%COMP%]   span[_ngcontent-%COMP%] {\n  width: 8px;\n  height: 8px;\n  border-radius: 50%;\n  background: #c7c7cc;\n  animation: _ngcontent-%COMP%_typing 1.4s infinite ease-in-out;\n}\n\n.typing-dots[_ngcontent-%COMP%]   span[_ngcontent-%COMP%]:nth-child(1) {\n  animation-delay: 0ms;\n}\n\n.typing-dots[_ngcontent-%COMP%]   span[_ngcontent-%COMP%]:nth-child(2) {\n  animation-delay: 200ms;\n}\n\n.typing-dots[_ngcontent-%COMP%]   span[_ngcontent-%COMP%]:nth-child(3) {\n  animation-delay: 400ms;\n}\n\n@keyframes _ngcontent-%C