import { Component, ViewChild, ElementRef, AfterViewChecked } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

interface QuestionSet {
  title: string;
  questions: string[];
  context: string;
}

@Component({
  selector: 'app-chatbot',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './chatbot.component.html',
  styleUrls: ['./chatbot.component.scss']
})
export class ChatbotComponent implements AfterViewChecked {
  @ViewChild('chatMessages') private chatMessages!: ElementRef;
  @ViewChild('messageInput') private messageInput!: ElementRef;
  
  messages: ChatMessage[] = [];
  currentMessage = '';
  isTyping = false;
  isRefreshing = false;
  currentQuestionSetIndex = 0;
  private shouldScrollToBottom = false;

  questionSets: QuestionSet[] = [
    {
      title: "Getting Started",
      context: "beginner",
      questions: [
        "How to start investing with $1000?",
        "What's the difference between stocks and bonds?",
        "Explain compound interest simply",
        "Best investment apps for beginners",
        "How much should I save before investing?",
        "What is diversification?"
      ]
    },
    {
      title: "Portfolio Management",
      context: "portfolio",
      questions: [
        "Help me rebalance my portfolio",
        "Best asset allocation for my age",
        "How to diversify across sectors?",
        "Should I invest in international markets?",
        "When to rebalance my investments?",
        "Tax-efficient portfolio strategies"
      ]
    },
    {
      title: "Market Analysis",
      context: "market",
      questions: [
        "What's the current market outlook?",
        "Which sectors are performing well?",
        "How do interest rates affect stocks?",
        "Is now a good time to invest?",
        "Market volatility strategies",
        "Economic indicators to watch"
      ]
    },
    {
      title: "Advanced Strategies",
      context: "advanced",
      questions: [
        "Options trading basics",
        "Value vs growth investing",
        "Dollar-cost averaging strategy",
        "Real estate investment trusts (REITs)",
        "ESG investing opportunities",
        "Cryptocurrency portfolio allocation"
      ]
    },
    {
      title: "Risk Management",
      context: "risk",
      questions: [
        "How to protect my portfolio in a recession?",
        "Stop-loss order strategies",
        "Emergency fund vs investments",
        "Insurance needs assessment",
        "Hedging strategies for beginners",
        "Risk tolerance evaluation"
      ]
    },
    {
      title: "Retirement Planning",
      context: "retirement",
      questions: [
        "401(k) vs Roth IRA comparison",
        "How much do I need for retirement?",
        "Social Security optimization",
        "Healthcare costs in retirement",
        "Retirement withdrawal strategies",
        "Catch-up contributions explained"
      ]
    }
  ];

  suggestions = [
    "What's the current market outlook?",
    "Help me diversify my portfolio",
    "Explain cryptocurrency investing",
    "Best long-term investment strategies",
    "How to start investing with $1000?",
    "Risk management techniques"
  ];

  get currentQuestionSet(): QuestionSet {
    return this.questionSets[this.currentQuestionSetIndex];
  }

  constructor(private router: Router) {
    // Start with empty messages to show welcome screen
  }

  selectSuggestion(suggestion: string) {
    this.currentMessage = suggestion;
    this.sendMessage();
  }

  sendMessage() {
    if (!this.currentMessage.trim() || this.isTyping) return;
    
    // Add user message
    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      type: 'user',
      content: this.currentMessage,
      timestamp: new Date()
    };
    this.messages.push(userMessage);
    this.shouldScrollToBottom = true;
    
    // Store current message for response generation
    const userInput = this.currentMessage;
    this.currentMessage = '';
    this.resizeTextarea();
    
    // Show typing indicator
    this.isTyping = true;
    this.shouldScrollToBottom = true;
    
    // Generate AI response after a realistic delay
    setTimeout(() => {
      const response = this.generateInvestmentResponse(userInput);
      const aiMessage: ChatMessage = {
        id: (Date.now() + 1).toString(),
        type: 'assistant',
        content: response,
        timestamp: new Date()
      };
      this.messages.push(aiMessage);
      this.isTyping = false;
      this.shouldScrollToBottom = true;
    }, 1500 + Math.random() * 1000); // 1.5-2.5 seconds for realistic feel
  }

  onKeyDown(event: KeyboardEvent) {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      this.sendMessage();
    }
  }

  onInput(event: any) {
    this.resizeTextarea();
  }

  private resizeTextarea() {
    if (this.messageInput?.nativeElement) {
      const textarea = this.messageInput.nativeElement;
      textarea.style.height = 'auto';
      textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';
    }
  }

  ngAfterViewChecked() {
    if (this.shouldScrollToBottom) {
      this.scrollToBottom();
      this.shouldScrollToBottom = false;
    }
  }

  private scrollToBottom(): void {
    try {
      if (this.chatMessages?.nativeElement) {
        this.chatMessages.nativeElement.scrollTop = this.chatMessages.nativeElement.scrollHeight;
      }
    } catch(err) {
      console.log('Could not scroll to bottom:', err);
    }
  }

  private generateInvestmentResponse(userInput: string): string {
    const input = userInput.toLowerCase();
    
    const responses = {
      portfolio: [
        "**Portfolio Diversification Strategy**\n\nBased on current market analysis, I recommend a balanced approach:\n\nâ€¢ **60% Equity**: Focus on large-cap growth stocks and ETFs\nâ€¢ **25% Fixed Income**: Government and corporate bonds\nâ€¢ **10% Alternative Assets**: REITs or commodity funds\nâ€¢ **5% Cash**: Emergency liquidity\n\nThis allocation provides growth potential while managing risk through diversification.",
        
        "**Modern Portfolio Theory Application**\n\nFor optimal risk-adjusted returns, consider:\n\nâ€¢ **Core Holdings** (70%): Low-cost index funds (S&P 500, Total Market)\nâ€¢ **Growth Allocation** (20%): Technology, healthcare, emerging markets\nâ€¢ **Defensive Allocation** (10%): Utilities, consumer staples, dividend stocks\n\nRebalance quarterly to maintain target allocations and capture market opportunities."
      ],
      
      market: [
        "**Current Market Outlook - Q4 2024**\n\nðŸ“ˆ **Key Trends:**\nâ€¢ AI and technology sector showing strong momentum\nâ€¢ Federal Reserve policy normalizing, creating opportunities\nâ€¢ Emerging markets gaining traction with improving fundamentals\nâ€¢ ESG investments demonstrating resilience\n\nâš ï¸ **Risks to Monitor:**\nâ€¢ Geopolitical tensions affecting energy markets\nâ€¢ Inflation expectations and interest rate sensitivity",
        
        "**Market Analysis & Opportunities**\n\nðŸŽ¯ **Sectors to Watch:**\nâ€¢ **Technology**: Cloud computing, cybersecurity, semiconductors\nâ€¢ **Healthcare**: Biotech innovations, aging population demographics\nâ€¢ **Clean Energy**: Renewable infrastructure, battery technology\nâ€¢ **Financial Services**: Digital banking, fintech disruption\n\nConsider dollar-cost averaging for volatile positions to optimize entry points."
      ],
      
      crypto: [
        "**Cryptocurrency Investment Framework**\n\nðŸ” **Fundamental Approach:**\nâ€¢ Limit crypto exposure to 5-10% of total portfolio\nâ€¢ Focus on established cryptocurrencies (Bitcoin, Ethereum)\nâ€¢ Understand blockchain technology and use cases\nâ€¢ Consider crypto ETFs for regulated exposure\n\nâš¡ **Risk Management:**\nâ€¢ High volatility requires smaller position sizes\nâ€¢ Never invest more than you can afford to lose\nâ€¢ Use hardware wallets for security\nâ€¢ Stay informed about regulatory developments"
      ],
      
      beginner: [
        "**Starting Your Investment Journey**\n\nðŸŽ¯ **Step-by-Step Guide:**\n\n1. **Emergency Fund**: Build 3-6 months of expenses in high-yield savings\n2. **401(k) Match**: Contribute enough to get full employer match\n3. **Index Funds**: Start with low-cost broad market ETFs\n4. **Gradual Growth**: Increase contributions by 1% annually\n\nðŸ’¡ **Beginner-Friendly Options:**\nâ€¢ Target-date funds for automatic rebalancing\nâ€¢ Robo-advisors for hands-off management\nâ€¢ Fractional shares to start with any amount",
        
        "**Investment Basics - Building Wealth Over Time**\n\nðŸ“š **Core Principles:**\nâ€¢ **Time in Market** beats timing the market\nâ€¢ **Compound Interest** is your greatest ally\nâ€¢ **Diversification** reduces risk without sacrificing returns\nâ€¢ **Low Costs** maximize your long-term wealth\n\nðŸš€ **Action Plan:**\nâ€¢ Open a brokerage account with low fees\nâ€¢ Set up automatic monthly investments\nâ€¢ Focus on learning, not quick profits\nâ€¢ Stay disciplined during market volatility"
      ],
      
      risk: [
        "**Risk Management Strategies**\n\nðŸ›¡ï¸ **Portfolio Protection:**\nâ€¢ **Asset Allocation**: Spread risk across asset classes\nâ€¢ **Position Sizing**: No single investment >5% of portfolio\nâ€¢ **Stop-Loss Orders**: Limit downside on individual positions\nâ€¢ **Hedging**: Consider protective puts or inverse ETFs\n\nðŸ“Š **Risk Assessment:**\nâ€¢ Evaluate your risk tolerance honestly\nâ€¢ Align investments with time horizon\nâ€¢ Regular portfolio stress testing\nâ€¢ Maintain adequate insurance coverage"
      ],
      
      default: [
        "**Investment Guidance & Analysis**\n\nI'm here to help with comprehensive financial planning. Here are some areas I can assist with:\n\nðŸ’¼ **Portfolio Management**\nâ€¢ Asset allocation strategies\nâ€¢ Risk assessment and diversification\nâ€¢ Rebalancing recommendations\n\nðŸ“Š **Market Insights**\nâ€¢ Sector analysis and trends\nâ€¢ Economic indicators impact\nâ€¢ Investment opportunities\n\nðŸŽ¯ **Personal Finance**\nâ€¢ Goal-based investing\nâ€¢ Tax-efficient strategies\nâ€¢ Retirement planning\n\nWhat specific aspect of investing would you like to explore?"
      ]
    };
    
    // Enhanced keyword matching
    if (input.includes('portfolio') || input.includes('diversif') || input.includes('allocation')) {
      return responses.portfolio[Math.floor(Math.random() * responses.portfolio.length)];
    } else if (input.includes('market') || input.includes('trend') || input.includes('outlook') || input.includes('sector')) {
      return responses.market[Math.floor(Math.random() * responses.market.length)];
    } else if (input.includes('crypto') || input.includes('bitcoin') || input.includes('ethereum') || input.includes('blockchain')) {
      return responses.crypto[0];
    } else if (input.includes('start') || input.includes('begin') || input.includes('new') || input.includes('1000') || input.includes('first')) {
      return responses.beginner[Math.floor(Math.random() * responses.beginner.length)];
    } else if (input.includes('risk') || input.includes('safe') || input.includes('protect') || input.includes('loss')) {
      return responses.risk[0];
    } else {
      return responses.default[0];
    }
  }

  formatMessage(content: string): string {
    // Convert markdown-style formatting to HTML
    return content
      .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
      .replace(/\*(.*?)\*/g, '<em>$1</em>')
      .replace(/â€¢/g, '&bull;')
      .replace(/\n/g, '<br>');
  }

  copyMessage(content: string) {
    // Remove HTML tags for copying plain text
    const plainText = content.replace(/<[^>]*>/g, '').replace(/&bull;/g, 'â€¢');
    navigator.clipboard.writeText(plainText).then(() => {
      // Could add a toast notification here
      console.log('Message copied to clipboard');
    });
  }

  formatTime(timestamp: Date): string {
    return timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  }

  goBack() {
    this.router.navigate(['/discover']);
  }
}
