import { Component } from "@angular/core";
import { CommonModule } from "@angular/common";

interface DividendItem {
  id: string;
  symbol: string;
  companyName: string;
  market: string;
  dividend: number;
  exDate?: string;
  recordDate?: string;
  paymentDate?: string;
  color: string;
  letter: string;
}

interface EarningsDay {
  date: string;
  displayDate: string;
  dividends: DividendItem[];
}

@Component({
  selector: "app-earnings",
  standalone: true,
  imports: [CommonModule],
  templateUrl: "./earnings.component.html",
  styleUrls: ["./earnings.component.scss"],
})
export class EarningsCalendarComponent {
  selectedDate = "10";
  selectedYear = "2025";
  selectedPeriod = "Month";
  activeEarningsTab = "Dividend";
  activeFilterTab = "Dividend";

  earningsData: DividendItem[] = [
    {
      id: "1",
      symbol: "S68.SI",
      companyName: "SGX",
      market: "SG",
      dividend: 0.105,
      exDate: "2025-10-16",
      color: "#4285f4",
      letter: "S",
    },
    {
      id: "2",
      symbol: "S68.SI",
      companyName: "SGX",
      market: "SG",
      dividend: 0.105,
      recordDate: "2025-10-17",
      color: "#4285f4",
      letter: "S",
    },
    {
      id: "3",
      symbol: "S68.SI",
      companyName: "SGX",
      market: "SG",
      dividend: 0.105,
      paymentDate: "2025-10-27",
      color: "#4285f4",
      letter: "S",
    },
    {
      id: "4",
      symbol: "HSHD.SI",
      companyName: "HSBC HK SDR 5to1",
      market: "SG",
      dividend: 0.0198,
      paymentDate: "2025-10-01",
      color: "#ff9800",
      letter: "H",
    },
    {
      id: "5",
      symbol: "W05.SI",
      companyName: "Wing Tai",
      market: "SG",
      dividend: 0.04,
      paymentDate: "2025-10-15",
      color: "#ff9800",
      letter: "W",
    },
  ];

  earningsTabs = ["Earnings", "Dividend", "Economy"];

  setEarningsTab(tab: string) {
    this.activeEarningsTab = tab;
    this.activeFilterTab = tab;
  }

  setPeriod(period: string) {
    this.selectedPeriod = period;
  }
}
