import { Component, ViewChild, ElementRef, AfterViewChecked } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

@Component({
  selector: 'app-chatbot',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './chatbot.component.html',
  styleUrls: ['./chatbot.component.scss']
})
export class ChatbotComponent implements AfterViewChecked {
  @ViewChild('chatMessages') private chatMessages!: ElementRef;
  @ViewChild('messageInput') private messageInput!: ElementRef;
  
  messages: ChatMessage[] = [];
  currentMessage = '';
  isTyping = false;
  private shouldScrollToBottom = false;

  suggestions = [
    "What's the current market outlook?",
    "Help me diversify my portfolio",
    "Explain cryptocurrency investing",
    "Best long-term investment strategies",
    "How to start investing with $1000?",
    "Risk management techniques"
  ];

  constructor(private router: Router) {
    // Start with empty messages to show welcome screen
  }

  selectSuggestion(suggestion: string) {
    this.currentMessage = suggestion;
    this.sendMessage();
  }

  sendMessage() {
    if (!this.currentMessage.trim() || this.isTyping) return;
    
    // Add user message
    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      type: 'user',
      content: this.currentMessage,
      timestamp: new Date()
    };
    this.messages.push(userMessage);
    this.shouldScrollToBottom = true;
    
    // Store current message for response generation
    const userInput = this.currentMessage;
    this.currentMessage = '';
    this.resizeTextarea();
    
    // Show typing indicator
    this.isTyping = true;
    this.shouldScrollToBottom = true;
    
    // Generate AI response after a realistic delay
    setTimeout(() => {
      const response = this.generateInvestmentResponse(userInput);
      const aiMessage: ChatMessage = {
        id: (Date.now() + 1).toString(),
        type: 'assistant',
        content: response,
        timestamp: new Date()
      };
      this.messages.push(aiMessage);
      this.isTyping = false;
      this.shouldScrollToBottom = true;
    }, 1500 + Math.random() * 1000); // 1.5-2.5 seconds for realistic feel
  }

  onKeyDown(event: KeyboardEvent) {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      this.sendMessage();
    }
  }

  onInput(event: any) {
    this.resizeTextarea();
  }

  private resizeTextarea() {
    if (this.messageInput?.nativeElement) {
      const textarea = this.messageInput.nativeElement;
      textarea.style.height = 'auto';
      textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';
    }
  }

  ngAfterViewChecked() {
    if (this.shouldScrollToBottom) {
      this.scrollToBottom();
      this.shouldScrollToBottom = false;
    }
  }

  private scrollToBottom(): void {
    try {
      if (this.chatMessages?.nativeElement) {
        this.chatMessages.nativeElement.scrollTop = this.chatMessages.nativeElement.scrollHeight;
      }
    } catch(err) {
      console.log('Could not scroll to bottom:', err);
    }
  }

  private generateInvestmentResponse(userInput: string): string {
    const input = userInput.toLowerCase();
    
    const responses = {
      portfolio: [
        "**Portfolio Diversification Strategy**\n\nBased on current market analysis, I recommend a balanced approach:\n\n‚Ä¢ **60% Equity**: Focus on large-cap growth stocks and ETFs\n‚Ä¢ **25% Fixed Income**: Government and corporate bonds\n‚Ä¢ **10% Alternative Assets**: REITs or commodity funds\n‚Ä¢ **5% Cash**: Emergency liquidity\n\nThis allocation provides growth potential while managing risk through diversification.",
        
        "**Modern Portfolio Theory Application**\n\nFor optimal risk-adjusted returns, consider:\n\n‚Ä¢ **Core Holdings** (70%): Low-cost index funds (S&P 500, Total Market)\n‚Ä¢ **Growth Allocation** (20%): Technology, healthcare, emerging markets\n‚Ä¢ **Defensive Allocation** (10%): Utilities, consumer staples, dividend stocks\n\nRebalance quarterly to maintain target allocations and capture market opportunities."
      ],
      
      market: [
        "**Current Market Outlook - Q4 2024**\n\nüìà **Key Trends:**\n‚Ä¢ AI and technology sector showing strong momentum\n‚Ä¢ Federal Reserve policy normalizing, creating opportunities\n‚Ä¢ Emerging markets gaining traction with improving fundamentals\n‚Ä¢ ESG investments demonstrating resilience\n\n‚ö†Ô∏è **Risks to Monitor:**\n‚Ä¢ Geopolitical tensions affecting energy markets\n‚Ä¢ Inflation expectations and interest rate sensitivity",
        
        "**Market Analysis & Opportunities**\n\nüéØ **Sectors to Watch:**\n‚Ä¢ **Technology**: Cloud computing, cybersecurity, semiconductors\n‚Ä¢ **Healthcare**: Biotech innovations, aging population demographics\n‚Ä¢ **Clean Energy**: Renewable infrastructure, battery technology\n‚Ä¢ **Financial Services**: Digital banking, fintech disruption\n\nConsider dollar-cost averaging for volatile positions to optimize entry points."
      ],
      
      crypto: [
        "**Cryptocurrency Investment Framework**\n\nüîê **Fundamental Approach:**\n‚Ä¢ Limit crypto exposure to 5-10% of total portfolio\n‚Ä¢ Focus on established cryptocurrencies (Bitcoin, Ethereum)\n‚Ä¢ Understand blockchain technology and use cases\n‚Ä¢ Consider crypto ETFs for regulated exposure\n\n‚ö° **Risk Management:**\n‚Ä¢ High volatility requires smaller position sizes\n‚Ä¢ Never invest more than you can afford to lose\n‚Ä¢ Use hardware wallets for security\n‚Ä¢ Stay informed about regulatory developments"
      ],
      
      beginner: [
        "**Starting Your Investment Journey**\n\nüéØ **Step-by-Step Guide:**\n\n1. **Emergency Fund**: Build 3-6 months of expenses in high-yield savings\n2. **401(k) Match**: Contribute enough to get full employer match\n3. **Index Funds**: Start with low-cost broad market ETFs\n4. **Gradual Growth**: Increase contributions by 1% annually\n\nüí° **Beginner-Friendly Options:**\n‚Ä¢ Target-date funds for automatic rebalancing\n‚Ä¢ Robo-advisors for hands-off management\n‚Ä¢ Fractional shares to start with any amount",
        
        "**Investment Basics - Building Wealth Over Time**\n\nüìö **Core Principles:**\n‚Ä¢ **Time in Market** beats timing the market\n‚Ä¢ **Compound Interest** is your greatest ally\n‚Ä¢ **Diversification** reduces risk without sacrificing returns\n‚Ä¢ **Low Costs** maximize your long-term wealth\n\nüöÄ **Action Plan:**\n‚Ä¢ Open a brokerage account with low fees\n‚Ä¢ Set up automatic monthly investments\n‚Ä¢ Focus on learning, not quick profits\n‚Ä¢ Stay disciplined during market volatility"
      ],
      
      risk: [
        "**Risk Management Strategies**\n\nüõ°Ô∏è **Portfolio Protection:**\n‚Ä¢ **Asset Allocation**: Spread risk across asset classes\n‚Ä¢ **Position Sizing**: No single investment >5% of portfolio\n‚Ä¢ **Stop-Loss Orders**: Limit downside on individual positions\n‚Ä¢ **Hedging**: Consider protective puts or inverse ETFs\n\nüìä **Risk Assessment:**\n‚Ä¢ Evaluate your risk tolerance honestly\n‚Ä¢ Align investments with time horizon\n‚Ä¢ Regular portfolio stress testing\n‚Ä¢ Maintain adequate insurance coverage"
      ],
      
      default: [
        "**Investment Guidance & Analysis**\n\nI'm here to help with comprehensive financial planning. Here are some areas I can assist with:\n\nüíº **Portfolio Management**\n‚Ä¢ Asset allocation strategies\n‚Ä¢ Risk assessment and diversification\n‚Ä¢ Rebalancing recommendations\n\nüìä **Market Insights**\n‚Ä¢ Sector analysis and trends\n‚Ä¢ Economic indicators impact\n‚Ä¢ Investment opportunities\n\nüéØ **Personal Finance**\n‚Ä¢ Goal-based investing\n‚Ä¢ Tax-efficient strategies\n‚Ä¢ Retirement planning\n\nWhat specific aspect of investing would you like to explore?"
      ]
    };
    
    // Enhanced keyword matching
    if (input.includes('portfolio') || input.includes('diversif') || input.includes('allocation')) {
      return responses.portfolio[Math.floor(Math.random() * responses.portfolio.length)];
    } else if (input.includes('market') || input.includes('trend') || input.includes('outlook') || input.includes('sector')) {
      return responses.market[Math.floor(Math.random() * responses.market.length)];
    } else if (input.includes('crypto') || input.includes('bitcoin') || input.includes('ethereum') || input.includes('blockchain')) {
      return responses.crypto[0];
    } else if (input.includes('start') || input.includes('begin') || input.includes('new') || input.includes('1000') || input.includes('first')) {
      return responses.beginner[Math.floor(Math.random() * responses.beginner.length)];
    } else if (input.includes('risk') || input.includes('safe') || input.includes('protect') || input.includes('loss')) {
      return responses.risk[0];
    } else {
      return responses.default[0];
    }
  }

  formatMessage(content: string): string {
    // Convert markdown-style formatting to HTML
    return content
      .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
      .replace(/\*(.*?)\*/g, '<em>$1</em>')
      .replace(/‚Ä¢/g, '&bull;')
      .replace(/\n/g, '<br>');
  }

  copyMessage(content: string) {
    // Remove HTML tags for copying plain text
    const plainText = content.replace(/<[^>]*>/g, '').replace(/&bull;/g, '‚Ä¢');
    navigator.clipboard.writeText(plainText).then(() => {
      // Could add a toast notification here
      console.log('Message copied to clipboard');
    });
  }

  formatTime(timestamp: Date): string {
    return timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  }

  goBack() {
    this.router.navigate(['/discover']);
  }
}
