import { Component } from "@angular/core";
import { CommonModule } from "@angular/common";
import { Router } from "@angular/router";

interface CourseDay {
  day: number;
  title: string;
  completed: boolean;
}

interface Stock {
  symbol: string;
  market: string;
  price: number;
  change: string;
  period: string;
  isPositive: boolean;
}

@Component({
  selector: "app-course",
  standalone: true,
  imports: [CommonModule],
  templateUrl: "./course.component.html",
  styleUrls: ["./course.component.scss"],
})
export class CourseComponent {
  selectedDay = 1;
  showDetailContent = true; // Show detailed content for Day 1
  
  courseDays: CourseDay[] = [
    { day: 1, title: "Day1.What is a stock ?", completed: true },
    { day: 2, title: "Day2.What is a fund ?", completed: false },
    { day: 3, title: "Day3.What is an ETF ?", completed: false },
    { day: 4, title: "Day4.What is an Reits ?", completed: false },
  ];

  stocks: Stock[] = [
    {
      symbol: "META",
      market: "US",
      price: 734.30,
      change: "+0.14%",
      period: "24H",
      isPositive: true,
    },
    {
      symbol: "NVDA",
      market: "US",
      price: 181.46,
      change: "+0.17%",
      period: "24H",
      isPositive: true,
    },
    {
      symbol: "00700",
      market: "HK",
      price: 618.000,
      change: "-1.98%",
      period: "",
      isPositive: false,
    },
  ];

  constructor(private router: Router) {}

  goBack() {
    if (this.showDetailContent) {
      this.showDetailContent = false;
    } else {
      this.router.navigate(["/academy"]);
    }
  }

  selectDay(day: number) {
    this.selectedDay = day;
    if (day === 1) {
      this.showDetailContent = true;
    } else {
      this.showDetailContent = false;
    }
    console.log('Selected day:', day);
  }
}
